import { Component, ElementRef, EventEmitter, Output, ViewChild } from '@angular/core';

@Component({
  selector: 'app-search',
  templateUrl: './search.component.html',
  styleUrls: ['./search.component.css']
})
export class SearchComponent {
  searchText: string = '';


  // 1. create an event

  @Output()
  searchTextChanged: EventEmitter<string> = new EventEmitter<string>();

  @ViewChild('searchInput') searchInputEl: ElementRef;

  onSearchTextChanged(){

  }

  updateSearchText(){
      // this.searchText = event.target.value;
      // console.log(inputEL.value);
      this.searchText = this.searchInputEl.nativeElement.value;

    this.searchTextChanged.emit(this.searchText)

  }
};
<div class="ekart--search--product">
    <input class="ekart-search-product-input"   #searchInput >
    <button class="btn btn-search" (click)="updateSearchText(searchInput)">Search</button>
</div>
<div class="ekart-search-result-for">
    <p *ngIf="searchText != ''"><strong>Search result for:</strong> {{ searchText }}</p>
</div>
