import { Component, EventEmitter, Output } from '@angular/core';

@Component({
  selector: 'app-search',
  templateUrl: './search.component.html',
  styleUrls: ['./search.component.css']
})
export class SearchComponent {
  searchText: string = '';


  // 1. create an event

  @Output()
  searchTextChanged: EventEmitter<string> = new EventEmitter<string>();

  onSearchTextChanged(){
    this.searchTextChanged.emit(this.searchText)
  }

  updateSearchText(event: any){
      this.searchText = event.target.value;
  }
}
<div class="ekart--search--product">
    <input class="ekart-search-product-input" [(ngModel)]="searchText" (input)="searchTextChanged   " >
    <button [ngClass]="{ 'btn': true, 'btn-search': searchText, 'btn-search-disabled': searchText }" [disabled]="!searchText">Search</button>
</div>
<div class="ekart-search-result-for">
    <p *ngIf="searchText != ''"><strong>Search result for:</strong> {{ searchText }}</p>
</div>
